import React, { useState } from 'react';
import Image from 'next/image';

interface OptimizedImageProps {
  src: string;
  alt: string;
  width?: number;
  height?: number;
  className?: string;
  priority?: boolean;
}

export const OptimizedImage: React.FC<OptimizedImageProps> = ({
  src,
  alt,
  width = 800,
  height = 600,
  className = '',
  priority = false
}) => {
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(false);

  // Placeholder blur data URL (dapat diganti dengan blur hash yang lebih baik)
  const blurDataURL = 'data:image/jpeg;base64,';

  return (
    <div className={`image-container ${isLoading ? 'loading' : ''} ${error ? 'error' : ''} ${className}`}>
      <Image
        src={error ? '/images/fallback.jpg' : src}
        alt={alt}
        width={width}
        height={height}
        className={`transition-opacity duration-300 ${isLoading ? 'opacity-0' : 'opacity-100'}`}
        loading={priority ? 'eager' : 'lazy'}
        onLoadingComplete={() => setIsLoading(false)}
        onError={() => setError(true)}
        placeholder="blur"
        blurDataURL={blurDataURL}
      />
      {isLoading && (
        <div className="absolute inset-0 bg-gray-200 animate-pulse" />
      )}
      {error && (
        <div className="absolute inset-0 flex items-center justify-center bg-gray-100">
          <span className="text-gray-500">Gambar tidak tersedia</span>
        </div>
      )}
    </div>
  );
};

export default OptimizedImage;
